

--
-- 模型数据 `cmf_cms_model`
--
CREATE TABLE IF NOT EXISTS `cmf_cms_model` (
    `id` int(10) NOT NULL AUTO_INCREMENT,
    `name` char(30) NOT NULL DEFAULT '' COMMENT '模型名称',
    `table` char(20) NOT NULL DEFAULT '' COMMENT '表名',
    `fields` text COMMENT '字段列表',
    `channeltpl` varchar(30) NOT NULL DEFAULT '' COMMENT '栏目页模板',
    `listtpl` varchar(30) NOT NULL DEFAULT '' COMMENT '列表页模板',
    `showtpl` varchar(30) NOT NULL DEFAULT '' COMMENT '详情页模板',
    `create_time` int(10) unsigned DEFAULT '0' COMMENT '创建时间',
    `update_time` int(10) unsigned DEFAULT '0' COMMENT '更新时间',
    `setting` text COMMENT '模型配置',
    PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COMMENT='cms应用 内容模型表';

--
-- 模型字段数据 `cmf_cms_fields`
--
CREATE TABLE IF NOT EXISTS `cmf_cms_fields` (
    `id` int(10) NOT NULL AUTO_INCREMENT,
    `model_id` int(10) NOT NULL DEFAULT '0' COMMENT '模型ID',
    `diyform_id` int(10) NOT NULL DEFAULT '0' COMMENT '表单ID',
    `name` char(30) NOT NULL DEFAULT '' COMMENT '名称',
    `type` varchar(30) NOT NULL DEFAULT '' COMMENT '类型',
    `title` varchar(30) NOT NULL DEFAULT '' COMMENT '标题',
    `content` text COMMENT '内容',
    `defaultvalue` varchar(100) NOT NULL DEFAULT '' COMMENT '默认值',
    `rule` varchar(100) DEFAULT '' COMMENT '验证规则',
    `msg` varchar(30) DEFAULT '0' COMMENT '错误消息',
    `ok` varchar(30) DEFAULT '0' COMMENT '成功消息',
    `tip` varchar(30) DEFAULT '' COMMENT '提示消息',
    `decimals` tinyint(1) DEFAULT NULL COMMENT '小数点',
    `length` mediumint(8) DEFAULT NULL COMMENT '长度',
    `minimum` smallint(6) DEFAULT NULL COMMENT '最小数量',
    `maximum` smallint(6) unsigned NOT NULL DEFAULT '0' COMMENT '最大数量',
    `extend` varchar(255) NOT NULL DEFAULT '' COMMENT '扩展信息',
    `list_order` int(10) NOT NULL DEFAULT '0' COMMENT '排序',
    `create_time` int(10) unsigned DEFAULT '0' COMMENT '添加时间',
    `update_time` int(10) unsigned DEFAULT '0' COMMENT '更新时间',
    `iscontribute` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否可投稿',
    `isfilter` tinyint(1) NOT NULL DEFAULT '0' COMMENT '筛选',
    `status` tinyint(3) unsigned NOT NULL DEFAULT '1' COMMENT '状态;1:已发布;0:未发布;',
    PRIMARY KEY (`id`),
    KEY `model_id` (`model_id`) USING BTREE,
    KEY `diyform_id` (`diyform_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COMMENT='cms应用 模型字段表';


--
-- 栏目数据 `cmf_cms_channel`
--
CREATE TABLE IF NOT EXISTS `cmf_cms_channel` (
    `id` int(10) NOT NULL AUTO_INCREMENT COMMENT '分类id',
    `type` enum('channel','page','link','list') DEFAULT NULL COMMENT '类型',
    `model_id` int(10) unsigned DEFAULT '0' COMMENT '模型ID',
    `parent_id` bigint(20) unsigned NOT NULL DEFAULT '0' COMMENT '分类父id',
    `post_count` bigint(20) unsigned NOT NULL DEFAULT '0' COMMENT '分类文章数',
    `status` tinyint(3) unsigned NOT NULL DEFAULT '1' COMMENT '状态,1:发布,0:不发布',
    `delete_time` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '删除时间',
    `list_order` int(10) NOT NULL DEFAULT '1000' COMMENT '排序',
    `name` varchar(200) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '' COMMENT '分类名称',
    `path` varchar(255) NOT NULL DEFAULT '' COMMENT '分类层级关系路径',
    `keywords` varchar(255) NOT NULL DEFAULT '',
    `description` varchar(255) NOT NULL DEFAULT '',
    `channeltpl` varchar(100) DEFAULT '' COMMENT '栏目页模板',
    `listtpl` varchar(100) NOT NULL DEFAULT '' COMMENT '列表页模板',
    `showtpl` varchar(100) NOT NULL DEFAULT '' COMMENT '详情页模板',
    `outlink` varchar(255) DEFAULT NULL COMMENT '外部链接',
    `more` text COMMENT '扩展属性',
    PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COMMENT='cms应用 栏目分类表';




--
-- 文章基础表数据 `cmf_cms_archives`
--
CREATE TABLE IF NOT EXISTS `cmf_cms_archives` (
    `id` int(10) NOT NULL AUTO_INCREMENT,
    `user_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '会员ID',
    `channel_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '栏目ID',
    `model_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '模型ID',
    `title` varchar(80) DEFAULT '' COMMENT '文章标题',
    `keywords` varchar(255) DEFAULT '' COMMENT '关键字',
    `description` varchar(255) DEFAULT '' COMMENT '描述',
    `list_order` int(10) unsigned DEFAULT '1000' COMMENT '权重',
    `hits` int(10) unsigned DEFAULT '0' COMMENT '浏览次数',
    `create_time` int(10) unsigned DEFAULT '0' COMMENT '创建时间',
    `update_time` int(10) unsigned DEFAULT '0' COMMENT '更新时间',
    `published_time` int(10) unsigned DEFAULT '0' COMMENT '发布时间',
    `delete_time` int(10) unsigned DEFAULT '0' COMMENT '删除时间',
    `status` tinyint(3) unsigned DEFAULT '1' COMMENT '状态;1:已发布;0:未发布;',
    `more` text COMMENT '扩展属性',
    PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COMMENT='cms应用 文章内容表';



--
-- 文章外表1数据 `cmf_cms_appnews`
--
CREATE TABLE IF NOT EXISTS `cmf_cms_appnews` (
    `id` int(10) NOT NULL AUTO_INCREMENT,
    `content` longtext,
    `istop` enum('1','0') DEFAULT '0' COMMENT '置顶',
    `author` varchar(255) DEFAULT '' COMMENT '作者',
    `intro` varchar(255) DEFAULT '' COMMENT '简介',
    `content1` text COMMENT '文章内容',
    `comment` int(255) DEFAULT NULL COMMENT '评论数',
    `date1` date DEFAULT NULL COMMENT '日期1',
    `time1` time DEFAULT NULL COMMENT '时间1',
    `datetime1` datetime DEFAULT NULL COMMENT '日期时间1',
    `pic1` varchar(255) DEFAULT '' COMMENT '图片1',
    `piclist` varchar(500) DEFAULT '' COMMENT '多图片',
    `file1` varchar(255) DEFAULT '' COMMENT '单文件',
    `morefile` varchar(500) DEFAULT '' COMMENT '多文件',
    `multframe` set('red','blue','yellow') DEFAULT 'red' COMMENT '复选框',
    `title_en` varchar(255) DEFAULT '' COMMENT '标题（英文）',
    `content_en` text COMMENT '内容（英文）',
    `intro_en` varchar(500) DEFAULT '' COMMENT '简介（英文）',
    PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COMMENT='新闻';


--
-- 文章外表2数据 `cmf_cms_appproducts`
--
CREATE TABLE IF NOT EXISTS `cmf_cms_appproducts` (
    `id` int(10) NOT NULL AUTO_INCREMENT,
    `content` longtext NOT NULL,
    `price` decimal(65,2) DEFAULT NULL COMMENT '价格',
    `istop` enum('1','0') DEFAULT '0' COMMENT '置顶',
    `title_en` varchar(255) DEFAULT '' COMMENT '英文（标题）',
    `content_en` text COMMENT '内容（英文）',
    PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='产品';


--
--	单页表数据 `cmf_cms_page`
--
CREATE TABLE IF NOT EXISTS `cmf_cms_page` (
    `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT 'ID',
    `user_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '会员id',
    `title` varchar(50) DEFAULT '' COMMENT '标题',
    `keywords` varchar(255) DEFAULT '' COMMENT '关键字',
    `description` varchar(255) DEFAULT '' COMMENT '描述',
    `content` text COMMENT '内容',
    `hits` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '点击',
    `likes` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '点赞',
    `dislikes` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '点踩',
    `comments` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '评论',
    `showtpl` varchar(50) DEFAULT '' COMMENT '视图模板',
    `create_time` int(10) unsigned DEFAULT '0' COMMENT '创建时间',
    `update_time` int(10) unsigned DEFAULT '0' COMMENT '更新时间',
    `published_time` int(10) unsigned DEFAULT '0' COMMENT '发布时间',
    `list_order` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '排序',
    `status` tinyint(3) unsigned NOT NULL DEFAULT '1' COMMENT '状态;1:已发布;0:未发布;',
    `more` text NOT NULL COMMENT '扩展属性',
    PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COMMENT='cms应用 单页表';


--
--	标签数据 `cmf_cms_tags`
--
CREATE TABLE IF NOT EXISTS `cmf_cms_tags` (
    `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT 'id',
    `name` varchar(20) CHARACTER SET utf8 NOT NULL DEFAULT '' COMMENT '标签名称',
    `archives` text CHARACTER SET utf8 NOT NULL COMMENT '文档ID集合',
    `nums` bigint(20) unsigned NOT NULL DEFAULT '0' COMMENT '标签文章数',
    PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COMMENT='cms应用 标签管理表';


